#!/usr/bin/env python3

dna = ''

dna = dna.upper()

numberofT = dna.count('T')


converted_dna = dna.replace('T','U')

numberofU =  converted_dna.count('U')

string = "There were {} number of T's in the old sequence, and {} number of U's in the new sequence"
new_string = string.format(numberofT, numberofU)
print(new_string)

print('Converted Sequence:',converted_dna)





