#!/usr/bin/env python3

#defining the variable as the dna sequence
dna = ''

#convert everything to uppercase
dna = dna.upper()

#set variables as the numbers of each nucleotide
As = dna.count('A')
Cs = dna.count('C')
Gs = dna.count('G')
Ts = dna.count('T')

#create string with the number of nucleotides
string = "There are {} A's, {} C's, {} G's, and {} T's in this sequence"
new_string = string.format(As,Cs,Gs,Ts)

#print string
print(new_string)




