#!/usr/bin/env python3
#inputting the dna sequence of interest
dna = ''

#finding start position defined as G rather than the cut position which is A and T. 
startposition = float(dna.find('GAATTC')) + 1
endposition = float(dna.find('GAATTC')) + 6

string = 'EcoR1 startPos:{} endPos:{}'
final_string = string.format(startposition,endposition)

print(final_string)
